class Request {

    get(url) {
      return fetch(url)
      .then((res) => res.json());
    }

    post(url, payload) {
      return fetch(url,{
        method: "POST",

      })
    }

    delete(url) {
      return fetch(url, {
        method: "DELETE",
        headers: {'Content-Type': 'application/json'}
      })
    }

}

export default Request;
